import React, {useState} from 'react'

count [text, setText] = useState('0');

export default function TextForm(props) {
  return (
    <div> 
        <h1>{props.heading}</h1>
        <div className="mb-3">
         
         <textarea className="form-control" id="myBox" rows="8"></textarea>
        </div>
        <button className="btn btn-primary">Conver to Upper Case</button>
    </div>
  )
}
